import './App.scss';
import MainContent from './MainContent';
import Header from './Header';
import {useEffect, useState} from 'react';

function App() {
  const [stories, setStories] = useState([]);

  const titles = [Popular, Recent, Upvoted];

  useEffect(() => {
    fetch("https://hacker-news.firebaseio.com/v0/topstories.json")
    .then(response => response.json()) 
    .then(ids => {
      ids.slice(0,30).map(id => {
        fetch(`https://hacker-news.firebaseio.com/v0/item/${id}.json`)
        .then(response => response.json())
        .then(data => {
          let keyword = longestWord(data.title);
          data.link = `https://loremflickr.com/200/150/${keyword}`;
          // let random = Math.floor(Math.random()*(999-100+1)+100);
          // data.link = `https://picsum.photos/id/${random}/250/150`;
          setStories(prevState => [...prevState, data]);
        })
      });
    });
  }, []);

  function longestWord(str) {
      var words = str.split(' ');
      return words.reduce((longest, currVal) => (currVal.length > longest.length) ? currVal : longest)   
  }

  return (
    <div className="app">
      <div className="app__container">
        <div className="app__header">
          <Header/>
        </div>
        <div className="app__content">
          {titles.map((title) => (
            <MainContent stories={stories}/>
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
