import React from 'react'
import './MainContent.scss'
import ThumbUpIcon from '@material-ui/icons/ThumbUp';

function MainContent({ stories }) {

    const formatDate = (unix_timestamp) => {
        var d=new Date();  // Gets the current time
        var nowTs = Math.floor(d.getTime()/1000); // getTime() returns milliseconds, and we need seconds, hence the Math.floor and division by 1000
        var seconds = nowTs-unix_timestamp;

        // more that two days
        if (seconds > 2*24*3600) {
           return "a few days ago";
        }
        // a day
        if (seconds > 24*3600) {
           return "yesterday";
        }

        if (seconds > 3600) {
            return Math.floor(seconds/3600) + " hours ago";
        }
        if (seconds > 1800) {
           return "Half an hour ago";
        }
        if (seconds > 60) {
           return Math.floor(seconds/60) + " minutes ago";
        }
    }

    return (
        <div>
            <div className="main__header">
                <img src="https://upload.wikimedia.org/wikipedia/commons/thumb/b/b2/Y_Combinator_logo.svg/1200px-Y_Combinator_logo.svg.png"/>
                <p>Developer News</p>
            </div>
            <div className="content__header__wrapper">
                <button class="btn btn-menu sort-by">Popular</button>
                <button class="btn btn-menu sort-by not-selected">Recent</button>
                <button class="btn btn-menu sort-by not-selected">Popular</button>
                <button class="btn btn-menu sort-by not-selected">Discussed</button>
            </div>
            <div className="table">
                {stories.map((story) => (
                    <div className="card" id={story.id}>
                        <div className="cardHeader">
                            <a href={story.url}>{story.title}</a>
                        </div>
                        <div className="author">
                            <span>by {story.by}</span>
                        </div>
                        <div className="imageView">
                            <img src={story.link}/>
                        </div>
                        <div className="cardInfo">
                            <div>
                                <img src="https://cdn3.iconfinder.com/data/icons/faticons/32/arrow-up-01-512.png"/>
                                <span>{story.score}</span>
                            </div>
                            <div>
                                <span>{formatDate(story.time)}</span>
                            </div>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    )
}

export default MainContent
